import React, { useState } from "react";

import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import "./Expenses.css";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2022");
  const [filteredExpenses, setFilteredExpenses] = useState(props.expenses);

  const onChangeFilterYearHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
    // create filtered copy to display
    let filteredArr = props.expenses.filter(
      (expense) => expense.date.toString().substring(11, 15) === filteredYear
    );
    setFilteredExpenses(filteredArr);
  };
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onChangeFilterYear={onChangeFilterYearHandler}
        />
        {filteredExpenses.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );
};

export default Expenses;
